function validationcareerfunc(formname) {
	var valfname = document.getElementById(formname + 'fname').value;
	var vallname = document.getElementById(formname + 'lname').value;
	var valphone = document.getElementById(formname + 'phonenu').value;
	var valemail = document.getElementById(formname + 'emailid').value;
	var valcity = document.getElementById(formname + 'cityval').value;
	var valcategory = document.getElementById(formname + 'categoryval').value;
	var valupload = document.getElementById(formname + 'uploadval').value;
	var regexname = /^[\w'\-,.][^0-9_!??/\\+=@#$%&*(){}|~<>;:[\]]*$/;

	if (valfname == null || valfname == "") {
		document.getElementById(formname + "lblfname").innerHTML = "Required field!"
	}
	else {
		if(regexname.test(valfname)) {
		document.getElementById(formname + "lblfname").innerHTML = "";	
		}
		else {
		document.getElementById(formname + "lblfname").innerHTML = "Invalid First Name";
		document.getElementById(formname + 'fname').value  = '';
		console.log(document.getElementById(formname + 'fname').value);
		return false;
		}
	} 

	if (vallname == null || vallname == "") {
		document.getElementById(formname + "lbllname").innerHTML = "Required field!"
	} else {
		if(regexname.test(vallname)) {
			document.getElementById(formname + "lbllname").innerHTML = "";	
			}
			else {
			document.getElementById(formname + "lbllname").innerHTML = "Invalid Last Name";
			document.getElementById(formname + 'lname').value  = '';
			console.log(document.getElementById(formname + 'lname').value);
			return false;
			}
	}

	if (valphone == null || valphone == "") {
		document.getElementById(formname + "lblphone").innerHTML = "Required field!"
	} else {
		var input = document.getElementById(formname + 'phonenu');
		var regexPhone = /^[789]\d{9}$/;
		if (regexPhone.test(input.value)) {
			document.getElementById(formname + "lblphone").innerHTML = ""
		} else {
			document.getElementById(formname + "lblphone").innerHTML = 'Invalid Phone Number';
			document.getElementById(formname + 'phonenu').value  = '';
			console.log(document.getElementById(formname + 'phonenu').value);
			return false;
		}
	}

	if (valemail == null || valemail == "") {
		document.getElementById(formname + "lblemail").innerHTML = "Required field!"
	} else {
		var input = document.getElementById(formname + 'emailid');
		var regexEmail = /^[_a-z0-9-]+(\.[_a-z0-9-]+)*@[a-z0-9-]+(\.[a-z0-9-]+)*(\.[a-z]{2,4})$/;
		if (regexEmail.test(input.value)) {
			document.getElementById(formname + "lblemail").innerHTML = ""
		} else {
			document.getElementById(formname + "lblemail").innerHTML = 'Invalid Email';
			document.getElementById(formname + 'emailid').value  = '';
			return false;
		}
	}

	if (valcity == null || valcity == "") {
		document.getElementById(formname + "lblcity").innerHTML = "Required field!"
	} else {
		if(regexname.test(valcity)) {
			document.getElementById(formname + "lblcity").innerHTML = "";	
			}
			else {
			document.getElementById(formname + "lblcity").innerHTML = "Invalid Job Location";
			return false;
			}
	}

	// if (valupload == null || valupload == "") {
	// 	document.getElementById(formname + "lblupload").innerHTML = "Required field!"
	// } 
	// else
	// {
	// 	document.getElementById(formname + "lblupload").innerHTML = 'Invalid File. Please select only Word or PDF File.';
	// 	return false;
	// }

	if (valcategory == null || valcategory == "") {
		document.getElementById(formname + "lblcategory").innerHTML = "Required field!"
	} else {
		if(regexname.test(valcategory)) {
			document.getElementById(formname + "lblcategory").innerHTML = "";	
			}
		else {
			document.getElementById(formname + "lblcategory").innerHTML = "Invalid Job Category";
			return false;
			}
	}

	if (valupload == null || valupload == "") {
		document.getElementById(formname + "lblupload").innerHTML = "Required field!"
	} 
	else {
		var input = document.getElementById(formname + 'uploadval');
		var regexUpload = /([a-zA-Z0-9\s_\\.\-:])+(.doc|.docx|.pdf)$/;
		if (regexUpload.test(input.value))
		 {
			document.getElementById(formname + "lblupload").innerHTML = "";
		}
		 else {
			document.getElementById(formname + "lblupload").innerHTML = 'Invalid file or file name. Please upload only Word (.doc or .docx) or PDF file name containing characters a-z, A-Z, 0-9, _, -, . only.';
			document.getElementById(formname + 'uploadval').value  = '';
			return false;
		}
	}
}

