function downloadFile(){
	var dd = document.getElementById("ddtbl");
	var tblname = dd.options[dd.selectedIndex].value;
	var casepdf1;
	if(tblname=="alliances_contact")
		casepdf1="alliances.xlsx";
	else if(tblname=="casestudydata")
		casepdf1="case_study.xlsx";
	else if(tblname=="tblcontactus")
		casepdf1="contact_us.xlsx";
	else if(tblname=="webinar")
		casepdf1="webinar.xlsx";
	else if(tblname=="webinar_inner")
		casepdf1="webinar_view.xlsx";
	else if(tblname=="confdata")
		casepdf1="confdata.xlsx";
	else if(tblname=="confsignupdata")
		casepdf1="confsignupdata.xlsx";
	var req = new XMLHttpRequest();
var ua = window.navigator.userAgent;
    var msie = ua.indexOf("Trident/");

    if (msie > 0) // If Internet Explorer, return version number
    {
		console.log("downloadfile");
		
		req.open("GET", "/assets/dbpdf/"+casepdf1, true);
		req.responseType = "blob";
		req.onload = function (event) {
		  var blob = req.response;
		  console.log(blob.size);
		  
		  window.navigator.msSaveBlob(blob,casepdf1);
		};	  
		req.send();
		
        
    }
    else  // If another browser, return 0
    {
		req.open("GET", "/assets/dbpdf/"+casepdf1, true);
		req.responseType = "blob";
		req.onload = function (event) {
		  var blob = req.response;
		  console.log(blob.size);
		  var link=document.createElement('a');
		  link.href=window.URL.createObjectURL(blob);
		  link.download=casepdf1;
		  link.click();
		};	  
		req.send();
    }
 
}

